mapper.insert:
INSERT INTO memberss(
    mem_id
    , mem_pw
    , mem_nm
    , mem_ph
    , mem_em
) VALUES(
    #{memId}
    , #{memPw}
    , #{memNm}
    , #{memPh}
    , #{memEm}
)

mapper.update:
UPDATE
    memberss
SET
    mem_nm = #{memNm}
    , mem_ph = #{memPh}
    , mem_em = #{memEm}

  , mem_password = #{memPassword}

WHERE mem_id = #{memId}
